body {
  height: 100vh;
  // border: #ddd solid 1px;
  // border-left: rgb(48, 56, 66) solid 1px;
  box-sizing: border-box;
  overflow: hidden;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;

  //  width: calc(100% - 5px);
  // height: calc(100% - 5px);
  //  background-color: rgba(0,255,255,1);
  // overflow: hidden;
  // box-shadow:2px 2px 2px rgb(128, 128, 128) , -2px -2px 2px rgb(128, 128, 128),-2px 2px 2px rgb(128, 128, 128), 2px -2px 2px rgb(128, 128, 128);
}
html {
  background-color: rgba(0, 0, 0, 0);
  padding: 0;
  box-sizing: border-box;
  overflow: hidden;
}
:focus{
  outline: none;
}
.common-height {
  height: 100%;
