  overflow: auto;
}
.user {
  overflow: auto;
}
.container {
  // min-height: 98vh;
  min-height: 100vh;
}
.commont-tab-wrap {
  display: inline-block;
  text-align: center;
  font-size: 16px;
  color: #151f34;
  border: 1px solid #2f54eb;
  border-radius: 3px;
  li {
    float: left;
    line-height: 22px;
    padding: 10px 22px;
    position: relative;
    border-right: 2px;
    user-select: none;
    &:not(:last-child) {
      &::after {
        content: '';
        display: inline-block;
        position: absolute;
        width: 2px;
        height: 10px;
        background: rgba(45, 83, 235, 1);
        border-radius: 1px;
        right: -1px;
        top: 50%;
        transform: translateY(-50%);
      }
    }
    &.router-link-active {
      background-color: #2f54eb;
      border-radius: 3px;
      color: #fff;
      &::after {
        display: none;
      }
    }
  }
}
.el-form--label-top .el-form-item__label {
  padding-bottom: 0;
}

.el-form-item__label {
  color: #151f34;
}
.el-input__inner,
.el-textarea__inner {
  background: rgba(247, 247, 250, 1);
  box-shadow: 0px 0px 1px 0px rgba(153, 153, 153, 0.3);
  border-radius: 4px;
  border: none;
  color: #151f34;
}
.el-textarea {
  .el-input__count {
    background: rgba(247, 247, 250, 1);
  }
}
.el-radio__inner {
  border: 1px solid rgba(21, 31, 52, 1);
}

.el-main {
  padding: 0;
}

// ws.js  window.vm.$alert样式-你已退出DiDi(居中显示)
