  border-bottom-left-radius: 6px;
}

.cu-capsule.radius .cu-tag:last-child::after,
.cu-capsule.radius .cu-tag[class*='line-'] {
  border-top-right-radius: 12px;
  border-bottom-right-radius: 12px;
}

.cu-capsule.round .cu-tag:first-child {
