}

.cu-chat .cu-item > .main .content::after {
  content: '';
  top: 27px;
  transform: rotate(45deg);
  position: absolute;
  z-index: 100;
  display: inline-block;
  overflow: hidden;
  width: 24px;
  height: 24px;
  left: -12px;
  right: initial;
  background-color: inherit;
}

.cu-chat .cu-item.self > .main .content::after {
