  right: -12px;
}

.cu-chat .cu-item > .main .content::before {
  content: '';
  top: 30px;
  transform: rotate(45deg);
  position: absolute;
  z-index: -1;
  display: inline-block;
  overflow: hidden;
  width: 24px;
  height: 24px;
  left: -12px;
  right: initial;
  background-color: inherit;
  filter: blur(5px);
  opacity: 0.3;
}

.cu-chat .cu-item > .main .content:not([class*='bg-'])::before {
  background-color: #333333;
