  ==================== */

.cu-card {
  display: block;
  overflow: hidden;
}

.cu-card > .cu-item {
  display: block;
  background-color: #ffffff;
  overflow: hidden;
  border-radius: 10px;
  margin: 30px;
}

.cu-card > .cu-item.shadow-blur {
  overflow: initial;
}

.cu-card.no-card > .cu-item {
  margin: 0px;
  border-radius: 0px;
}

.cu-card .grid.grid-square {
  margin-bottom: -20px;
}

.cu-card.case .image {
  position: relative;
}

.cu-card.case .image image {
  width: 100%;
}

.cu-card.case .image .cu-tag {
  position: absolute;
  right: 0;
  top: 0;
}

.cu-card.case .image .cu-bar {
  position: absolute;
  bottom: 0;
  width: 100%;
  background-color: transparent;
  padding: 0px 30px;
}

.cu-card.case.no-card .image {
  margin: 30px 30px 0;
  overflow: hidden;
  border-radius: 10px;
}

.cu-card.dynamic {
  display: block;
}

.cu-card.dynamic > .cu-item {
  display: block;
  background-color: #ffffff;
  overflow: hidden;
}

.cu-card.dynamic > .cu-item > .text-content {
  padding: 0 30px 0;
  max-height: 6.4em;
  overflow: hidden;
  font-size: 30px;
  margin-bottom: 20px;
}

.cu-card.dynamic > .cu-item .square-img {
  width: 100%;
  height: 200px;
  border-radius: 6px;
}

.cu-card.dynamic > .cu-item .only-img {
  width: 100%;
  height: 320px;
  border-radius: 6px;
}

