}

.cu-card.article > .cu-item {
  padding-bottom: 30px;
}

.cu-card.article > .cu-item .title {
  font-size: 30px;
  font-weight: 900;
  color: #333333;
  line-height: 100px;
  padding: 0 30px;
}

.cu-card.article > .cu-item .content {
  display: flex;
  padding: 0 30px;
}

.cu-card.article > .cu-item .content > image {
